  color: 'white',
  background: 'black',
  border: 'none',
  borderRadius: '50%',
  padding: '1rem 2rem',
});

export const ButtonSpan = (theme: any) => ({
  color: 'white',
  background: 'black',
  border: 'none',
  borderRadius: '50%',
});
